    next_mux_in_large[0] = in;

end

genvar gi;
// generate and endgenerate is optional
// generate (optional)
for (gi=0; gi<`length; gi=gi+1) begin : genbit
    assign mux_in[gi] = mux_in_large[(gi+1)*`scale-1];
end

assign done = (sel_bits==`pow2n-1);
assign reset = clock_s;

// prepare the input
always_ff @(posedge clock_d) begin
    if(clock_s) begin // like a combo of reset & enable
        mux_in_large      <= next_mux_in_large;
        out         <= 0;
    end
    else begin
        out <= next_out;
    end
end

endmodule